import Layout from "../../layouts/main.vue";
import appConfig from "../../../app.config";
export default {
  page: {
    title: "Mailbox",
    meta: [{
      name: "description",
      content: appConfig.description
    }]
  },
  data: function data() {
    return {
      title: "Mailbox",
      items: [{
        text: "Apps",
        href: "/"
      }, {
