import axios from 'axios'
import { authHeader } from '../helpers/authservice/auth-header';

config = {
    headers: authHeader()
};
class ComopanyDataService {

    getAll() {
        return axios.get('/api/v1/companies', config)
    }

    get(id) {
        return axios.get(`/api/v1/companies/${id}`, config);
    }

    create(data) {
        return axios.post("/api/v1/companies", data, config);
    }

    update(id, data) {
        return axios.put(`/api/v1/companies/${id}`, data, config);
    }

    delete(id) {
        return axios.delete(`/api/v1/companies/${id}`, config);
    }

    deleteAll() {
        return axios.delete(`/api/v1/companies`, config);
    }

    findByTitle(title) {
        return axios.get(`/api/v1/companies?title=${title}`, config);
    }
}

export default new ComopanyDataService();
