import axios from 'axios'
import { authHeader } from '../helpers/authservice/auth-header';


class CompaniesDataService {

    getAll(fields, sorts, pages) {

        let dataVals = {
            pages
        };


        let config = {
            headers: authHeader(),

        }
        console.log(dataVals)
        return axios.post('/api/v3/companies/list', pages)
    }

    get(id) {
        let config = {
            headers: authHeader()
        }
        return axios.get(`/api/v3/companies/get/${id}`, config);
    }

    create(data) {


        return axios.post(`/api/v3/companies/add`, data);
    }

    update(id, data) {
        console.log(data)
        return axios.put(`/api/v3/companies/update/${id}`, data);
    }

    delete(id) {

        return axios.delete(`/api/v3/companies/delete/${id}`);
    }

    deleteAll() {
        return axios.post(`/api/v2/companies`);
    }

    findByBrandName(name) {
        return axios.post(`/api/v2/companies?name=${name}`);
    }
}

export default new CompaniesDataService();
