import axios from 'axios'
import { authHeader } from '../helpers/authservice/auth-header';



class CountryDataService {
    config() {

        return { headers: authHeader() }
    };
    getAll() {
        return axios.get('/api/v2/products', config())
    }

    get(id) {
        return axios.get(`/api/v2/products/${id}`, config());
    }

    create(data) {
        return axios.post("/api/v2/products", data, config());
    }

    update(id, data) {
        return axios.put(`/api/v2/products/${id}`, data, config());
    }

    delete(id) {
        return axios.delete(`/api/v2/products/${id}`, config());
    }

    deleteAll() {
        return axios.delete(`/api/v2/products`, config());
    }

    findByTitle(title) {
        return axios.get(`/api/v2/products?title=${title}`, config);
    }
}

export default new CountryDataService();
